INSERT INTO `newsinfo` VALUES (85, 20, 0, 1, '龙行皇城－北京『挂四纯玩』双飞五日至尊游', '', '<br />\r\n<img src="/upload/other/3348893657.jpg" alt="" /><br />\r\n<br />\r\n<table width="95%" cellspacing="0" cellpadding="0" border="1">\r\n    <tbody>\r\n        <tr style="height: 18.75pt">\r\n            <td width="100" style="border-bottom: #cfcfcf 1pt inset; border-left: #cfcfcf 1pt inset; padding-bottom: 0cm; padding-left: 2.25pt; width: 75pt; padding-right: 0cm; background: #efefef; height: 18.75pt; border-top: #cfcfcf 1pt inset; border-right: #cfcfcf 1pt inset; padding-top: 0cm">\r\n            <div>04-07 行程</div>\r\n            </td>\r\n            <td width="429" style="border-bottom: #cfcfcf 1pt inset; border-left: #cfcfcf 1pt inset; padding-bottom: 0cm; padding-left: 2.25pt; width: 321.75pt; padding-right: 0cm; background: #efefef; height: 18.75pt; border-top: #cfcfcf 1pt inset; border-right: #cfcfcf 1pt inset; padding-top: 0cm">\r\n            <div>住:【住宿】北京挂牌四星级酒店</div>\r\n            </td>\r\n            <td width="150" style="border-bottom: #cfcfcf 1pt inset; border-left: #cfcfcf 1pt inset; padding-bottom: 0cm; padding-left: 2.25pt; width: 112.5pt; padding-right: 0cm; background: #efefef; height: 18.75pt; border-top: #cfcfcf 1pt inset; border-right: #cfcfcf 1pt inset; padding-top: 0cm">\r\n            <div>餐:晚</div>\r\n            </td>\r\n        </tr>\r\n        <tr style="height: 18.75pt">\r\n            <td colspan="3" style="border-bottom: #cfcfcf 1pt inset; border-left: #cfcfcf 1pt inset; padding-bottom: 0cm; background-color: transparent; padding-left: 2.25pt; padding-right: 0cm; height: 18.75pt; border-top: #cfcfcf 1pt inset; border-right: #cfcfcf 1pt inset; padding-top: 0cm">\r\n            <div align="center">\r\n            <table width="95%" cellspacing="0" cellpadding="0" border="0" style="width: 95%">\r\n                <tbody>\r\n                    <tr style="height: 18.75pt">\r\n                        <td style="border-bottom: #f0f0f0; border-left: #f0f0f0